import React, { useState, useEffect } from 'react';
import Navbar from './Navbar';
import './Plan.css';

function Plan() {
  const [formData, setFormData] = useState({
    destination: '',
    startDate: '',
    endDate: '',
  });

  const [itinerary, setItinerary] = useState([]);
  const [isTripPlanned, setIsTripPlanned] = useState(false);
  const [plannedDestination, setPlannedDestination] = useState('');

  useEffect(() => {
    // Set the "Start Date" input to the current date in the correct format (YYYY-MM-DD).
    const currentDate = new Date();
    const year = currentDate.getFullYear();
    const month = String(currentDate.getMonth() + 1).padStart(2, '0');
    const day = String(currentDate.getDate()).padStart(2, '0');
    const formattedDate = `${year}-${month}-${day}`;
    setFormData({
      ...formData,
      startDate: formattedDate,
    });
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const { destination, startDate, endDate } = formData;

    if (!destination) {
      alert("Please select a valid destination.");
      return;
    }

    // Simulated trip planning logic
    const simulatedItinerary = [
      { day: 'Day 1', activity: 'Visit Museum' },
      { day: 'Day 2', activity: 'Explore Landmarks' },
      { day: 'Day 3', activity: 'Relax on the Beach' },
    ];

    setItinerary(simulatedItinerary);
    setIsTripPlanned(true);
    setPlannedDestination(destination);
  };

  const destinationOptions = [
    'New York',
    'Paris',
    'Tokyo',
    'London',
    // Add more destination options as needed
  ];

  return (
    <div className="trip-planner">
      <Navbar />
      {isTripPlanned ? (
        <div className="trip-itinerary">
          <h3>Your Trip Itinerary:</h3>
          <p>Destination: {plannedDestination}</p>
          <table>
            <thead>
              <tr>
                <th>Day</th>
                <th>Activity</th>
              </tr>
            </thead>
            <tbody>
              {itinerary.map((item, index) => (
                <tr key={index}>
                  <td>{item.day}</td>
                  <td>{item.activity}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <div className="left-rectangle">
          <form onSubmit={handleSubmit}>
            <h2>Plan Your Trip</h2>
            <div className="form-group">
              <label htmlFor="destination">Destination:</label>
              <div className="input-wrapper">
                <select
                  name="destination"
                  value={formData.destination}
                  onChange={handleInputChange}
                  required
                >
                  <option value="">Select a destination</option>
                  {destinationOptions.map((option, index) => (
                    <option key={index} value={option}>
                      {option}
                    </option>
                  ))}
                </select>
              </div>
            </div>
            <div className="form-group">
              <label htmlFor="startDate">Start Date:</label>
              <input
                type="date"
                name="startDate"
                value={formData.startDate}
                onChange={handleInputChange}
                min={formData.startDate}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="endDate">End Date:</label>
              <input
                type="date"
                name="endDate"
                value={formData.endDate}
                onChange={handleInputChange}
                required
              />
            </div>
            <button type="submit">Plan Trip</button>
          </form>
        </div>
      )}
      {isTripPlanned && (
        <div className="circle">
          <a href="https://www.makemytrip.com" target="_blank" rel="noopener noreferrer">
            Book Tickets
          </a>
        </div>
      )}
    </div>
  );
}

export default Plan;
