import React, { useState } from 'react';
import Navbar from './Navbar';
import './Plan.css';

function Plan() {
  const [formData, setFormData] = useState({
    destination: '',
    startDate: '',
    endDate: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle the form submission, e.g., send the data to a server

    // Define the BookMyShow URL based on the user's input (you may need to construct this URL)
    const bookMyShowUrl = `https://www.bookmyshow.com/${formData.destination}`;

    // Redirect the user to the BookMyShow website
    window.location.href = bookMyShowUrl;
  };

  return (
    <div className="trip-planner">
      <Navbar />
      <div className="left-rectangle">
        <form onSubmit={handleSubmit}>
          <h2>Plan Your Trip</h2>
          <div className="form-group">
            <label htmlFor="destination">Destination:</label>
            <input
              type="text"
              name="destination"
              value={formData.destination}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="startDate">Start Date:</label>
            <input
              type="date"
              name="startDate"
              value={formData.startDate}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="endDate">End Date:</label>
            <input
              type="date"
              name="endDate"
              value={formData.endDate}
              onChange={handleInputChange}
              required
            />
          </div>
          <button type="submit">Plan Trip</button>
        </form>
      </div>
      <div className="circle">
        <h2>Book Tickets!</h2>
        {/* Example: Direct link to BookMyShow */}
        <a href="https://www.bookmyshow.com" target="_blank" rel="noopener noreferrer">
          Book Tickets
        </a>
      </div>
    </div>
  );
}

export default Plan;
