import './Login.css';
import { Amplify } from 'aws-amplify';
import { awsExports } from './aws-export';
import { Authenticator } from '@aws-amplify/ui-react';
import '@aws-amplify/ui-react/styles.css';
import Navbar from './Navbar';
import { Auth, signOut } from 'aws-amplify';


Amplify.configure({
  Auth: {
    region: awsExports.REGION,
    userPoolId: awsExports.USER_POOL_ID,
    userPoolWebClientId: awsExports.USER_POOL_WEB_CLIENT_ID
  }
});


function Login() {
  const user = Auth.user;

  return (
    <>
      {user && (
        <div>Welcome {user.username}
          <button onClick={signOut}>Sign out</button>
        </div>
      )}

      {!user && (
        <Authenticator initialState='signIn'
          components={{
            SignUp: {
              FormFields() {

                return (
                  <>
                    <Navbar />
                    <Authenticator.SignUp.FormFields />

                    {/* Custom fields for given_name and family_name */}
                    <div><label>First name</label></div>
                    <input
                      type="text"
                      name="given_name"
                      placeholder="Please enter your first name"
                    />
                    <div><label>Last name</label></div>
                    <input
                      type="text"
                      name="family_name"
                      placeholder="Please enter your last name"
                    />
                    <div><label>Email</label></div>
                    <input
                      type="text"
                      name="email"
                      placeholder="Please enter a valid email"
                    />


                  </>
                )
              }
            }
          }}
          services={{
            async validateCustomSignUp(formData) {
              if (!formData.given_name) {
                return {
                  given_name: 'First Name is required',
                };
              }
              if (!formData.family_name) {
                return {
                  family_name: 'Last Name is required',
                };
              }
              if (!formData.email) {
                return {
                  email: 'Email is required',
                };
              }
            }
          }}
        >
          {({ signOut, user }) => (
            <div>Welcome {user.username}
              <button onClick={signOut}>Sign out</button>
            </div>
          )}
        </Authenticator>
      )}
    </>
  );
}

export default Login;
