import React, { useState } from 'react';
import Navbar from './Navbar';
import './Plan.css';

function Plan() {
  const [formData, setFormData] = useState({
    destination: '',
    startDate: '',
    endDate: '',
  });

  const [itinerary, setItinerary] = useState([]);
  const [isTripPlanned, setIsTripPlanned] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const { destination, startDate, endDate } = formData;

    if (!destination) {
      alert("Please enter a valid destination.");
      return;
    }

    // Simulated trip planning logic
    const simulatedItinerary = [
      { day: 'Day 1', activity: 'Visit Museum' },
      { day: 'Day 2', activity: 'Explore Landmarks' },
      { day: 'Day 3', activity: 'Relax on the Beach' },
    ];

    setItinerary(simulatedItinerary);
    setIsTripPlanned(true);
  };

  return (
    <div className="trip-planner">
      <Navbar />
      {isTripPlanned ? ( // Render this section when isTripPlanned is true
        <div className="trip-itinerary">
          <h3>Your Trip Itinerary:</h3>
          <table>
            <thead>
              <tr>
                <th>Day</th>
                <th>Activity</th>
              </tr>
            </thead>
            <tbody>
              {itinerary.map((item, index) => (
                <tr key={index}>
                  <td>{item.day}</td>
                  <td>{item.activity}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <div className="left-rectangle">
          <form onSubmit={handleSubmit}>
            <h2>Plan Your Trip</h2>
            <div className="form-group">
              <label htmlFor="destination">Destination:</label>
              <input
                type="text"
                name="destination"
                value={formData.destination}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="startDate">Start Date:</label>
              <input
                type="date"
                name="startDate"
                value={formData.startDate}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="endDate">End Date:</label>
              <input
                type="date"
                name="endDate"
                value={formData.endDate}
                onChange={handleInputChange}
                required
              />
            </div>
            <button type="submit">Plan Trip</button>
          </form>
        </div>
      )}
      {isTripPlanned && (
        <div className="circle">
          <a href="https://www.makemytrip.com" target="_blank" rel="noopener noreferrer">
            Book Tickets
          </a>
        </div>
      )}
    </div>
  );
}

export default Plan;
