import { useState, useEffect } from 'react'; // Import useState and useEffect
import './App.css';
import Navbar from './Navbar';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import Login from "./Login";

function App() {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const images = [
    '/img1.jpeg',
    '/img2.jpeg',
    '/img3.jpeg',
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 5000);

    return () => {
      clearInterval(interval);
    };
  }, [images.length]);

  const backgroundImageUrl = `${process.env.PUBLIC_URL}${images[currentImageIndex]}`;

  const bannerStyle = {
    backgroundColor: '#333',
    flex: 2,
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    color: 'white',
    backgroundImage: `url(${process.env.PUBLIC_URL}${images[currentImageIndex]})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
  };

  return (
    <div className="App">
      <Navbar />
      <div style={bannerStyle}></div>
      <div className="sections">
        <div className="section" style={{ backgroundColor: '#708C82' }}>
          {/* Section 1 content */}
        </div>
        <div className="section" style={{ backgroundColor: '#F5C2C2' , flex: '1 1 50%'}}>
          {/* Section 2 content */}
        </div>
      </div>
    </div>
  );
}

export default App;
