import React, { useState, useEffect } from 'react';
import Navbar from './Navbar';
import Papa from 'papaparse'; // Import Papaparse
import './Plan.css';

function Plan() {
  const [formData, setFormData] = useState({
    destination: '',
    startDate: '',
    endDate: '',
  });

  const [itinerary, setItinerary] = useState([]);
  const [isTripPlanned, setIsTripPlanned] = useState(false);
  const [csvData, setCsvData] = useState([]); // State to store CSV data

  useEffect(() => {
    // Load CSV data when the component mounts
    Papa.parse('/your-csv-file.csv', {
      download: true,
      header: true,
      dynamicTyping: true,
      complete: function (results) {
        setCsvData(results.data);
      },
    });
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const { destination, startDate, endDate } = formData;

    if (!destination) {
      alert("Please enter a valid destination.");
      return;
    }

    // Filter the CSV data based on the destination
    const filteredData = csvData.filter((entry) => {
      const city = entry["City"];
      const state = entry["State"];

      if (city && state) {
        return city.toLowerCase() === destination.toLowerCase() || state.toLowerCase() === destination.toLowerCase();
      }
      return false;
    });

    if (filteredData.length === 0) {
      alert("No matching locations found.");
      return;
    }

    // Perform your trip planning logic using filteredData

    const simulatedItinerary = [
      // Replace with your trip planning logic
      { day: 'Day 1', activity: 'Visit Museum' },
      { day: 'Day 2', activity: 'Explore Landmarks' },
      { day: 'Day 3', activity: 'Relax on the Beach' },
    ];

    setItinerary(simulatedItinerary);
    setIsTripPlanned(true);
  };

  return (
    <div className="trip-planner">
      <Navbar />
      <div className="left-rectangle">
        <form onSubmit={handleSubmit}>
          <h2>Plan Your Trip</h2>
          <div className="form-group">
            <label htmlFor="destination">Destination:</label>
            <input
              type="text"
              name="destination"
              value={formData.destination}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="startDate">Start Date:</label>
            <input
              type="date"
              name="startDate"
              value={formData.startDate}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="endDate">End Date:</label>
            <input
              type="date"
              name="endDate"
              value={formData.endDate}
              onChange={handleInputChange}
              required
            />
          </div>
          <button type="submit">Plan Trip</button>
        </form>
      </div>
      <div className="circle">
        <a href="https://www.makemytrip.com" target="_blank" rel="noopener noreferrer">
          Book Tickets
        </a>
      </div>
      {isTripPlanned && (
        <div className="trip-itinerary">
          <h3>Your Trip Itinerary:</h3>
          <table>
            <thead>
              <tr>
                <th>Day</th>
                <th>Activity</th>
              </tr>
            </thead>
            <tbody>
              {itinerary.map((item, index) => (
                <tr key={index}>
                  <td>{item.day}</td>
                  <td>{item.activity}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default Plan;
