import React, { useState } from 'react';
import Navbar from './Navbar';
import './Plan.css';

function Plan() {
  const [formData, setFormData] = useState({
    destination: '',
    startDate: '',
    endDate: '',
  });

  const [itinerary, setItinerary] = useState([]);
  const [isTripPlanned, setIsTripPlanned] = useState(false);
  const [plannedDestination, setPlannedDestination] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const { destination, startDate, endDate } = formData;

    if (!destination || !startDate || !endDate) {
      alert("Please fill in all fields.");
      return;
    }

    // Calculate the number of days between start and end dates
    const start = new Date(startDate);
    const end = new Date(endDate);
    const dayDiff = Math.ceil((end - start) / (1000 * 60 * 60 * 24));

    if (dayDiff < 1) {
      alert("End date must be after the start date.");
      return;
    }

    const simulatedItinerary = getItinerary(destination, dayDiff);

    setItinerary(simulatedItinerary);
    setIsTripPlanned(true);
    setPlannedDestination(destination);
  };

  const destinationOptions = [
    'Andhra Pradesh',
    'Arunachal Pradesh',
    'Assam',
    'Bihar',
    'Chhattisgarh',
    'Goa',
    'Gujarat',
    'Haryana',
  ];

  const getItinerary = (destination, numDays) => {
    const itineraries = {
      // Your existing itinerary data here
    };

    // Limit the itinerary to the specified number of days
    const selectedItinerary = itineraries[destination] || [];
    return selectedItinerary.slice(0, numDays);
  };

  return (
    <div className="trip-planner">
      <Navbar />
      {isTripPlanned ? (
        <div className="trip-itinerary">
          <h3>Your Trip Itinerary:</h3>
          <p>Destination: {plannedDestination}</p>
          <table>
            <thead>
              <tr>
                <th>Day</th>
                <th>Activity</th>
              </tr>
            </thead>
            <tbody>
              {itinerary.map((item, index) => (
                <tr key={index}>
                  <td>{item.day}</td>
                  <td>{item.activity}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <div className="left-rectangle">
          <form onSubmit={handleSubmit}>
            <h2>Plan Your Trip</h2>
            <div className="form-group">
              <label htmlFor="destination">Destination:</label>
              <div className="input-wrapper">
                <select
                  name="destination"
                  value={formData.destination}
                  onChange={handleInputChange}
                  required
                >
                  <option value="">Select a destination</option>
                  {destinationOptions.map((option, index) => (
                    <option key={index} value={option}>
                      {option}
                    </option>
                  ))}
                </select>
              </div>
            </div>
            <div className="form-group">
              <label htmlFor="startDate">Start Date:</label>
              <input
                type="date"
                name="startDate"
                value={formData.startDate}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="endDate">End Date:</label>
              <input
                type="date"
                name="endDate"
                value={formData.endDate}
                onChange={handleInputChange}
                required
              />
            </div>
            <button type="submit">Plan Trip</button>
          </form>
        </div>
      )}
      {isTripPlanned && (
        <div className="circle">
          <a href="https://www.makemytrip.com" target="_blank" rel="noopener noreferrer">
            Book Tickets
          </a>
        </div>
      )}
    </div>
  );
}

export default Plan;
