import React, { useState } from 'react';
import Navbar from './Navbar';
import './Plan.css';

function Plan() {
  const [formData, setFormData] = useState({
    destination: '',
    startDate: '',
    endDate: '',
  });

  const [itinerary, setItinerary] = useState([]);
  const [isTripPlanned, setIsTripPlanned] = useState(false);
  const [plannedDestination, setPlannedDestination] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const { destination, startDate, endDate } = formData;

    if (!destination) {
      alert("Please select a valid destination.");
      return;
    }

    const simulatedItinerary = getItinerary(destination);

    setItinerary(simulatedItinerary);
    setIsTripPlanned(true);
    setPlannedDestination(destination);
  };

  const destinationOptions = [
    'Andhra Pradesh',
    'Arunachal Pradesh',
    'Assam',
    'Bihar',
    'Chhattisgarh',
    'Goa',
    'Gujarat',
    'Haryana',
  ];

  const getItinerary = (destination) => {
    // Your existing getItinerary function remains the same
    // ...

    return itineraries[destination] || [];
  };

  const searchFlights = () => {
    const { destination, startDate } = formData;

    if (!destination || !startDate) {
      alert("Please select a valid destination and start date.");
      return;
    }

    // Construct a Google search URL for flights
    const searchQuery = `${destination} to your_destination&departure_date=${startDate}`;
    const googleFlightsURL = `https://www.google.com/flights?hl=en#flt=${searchQuery}`;

    // Open the URL in a new tab
    window.open(googleFlightsURL, '_blank');
  };

  return (
    <div className="trip-planner">
      <Navbar />
      {isTripPlanned ? (
        <div className="trip-itinerary">
          <h3>Your Trip Itinerary:</h3>
          <p>Destination: {plannedDestination}</p>
          <table>
            <thead>
              <tr>
                <th>Day</th>
                <th>Activity</th>
              </tr>
            </thead>
            <tbody>
              {itinerary.map((item, index) => (
                <tr key={index}>
                  <td>{item.day}</td>
                  <td>{item.activity}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <div className="left-rectangle">
          <form onSubmit={handleSubmit}>
            {/* ... (your existing form code) */}
            <button type="submit">Plan Trip</button>
          </form>
        </div>
      )}
      {isTripPlanned && (
        <div className="circle">
          <a href="https://www.makemytrip.com" target="_blank" rel="noopener noreferrer">
            Book Tickets
          </a>
        </div>
      )}

      {isTripPlanned && (
        <div className="flight-search">
          <button onClick={searchFlights}>Search Flights</button>
        </div>
      )}
    </div>
  );
}

export default Plan;
